<script>
// Wait for the HTML document to be fully loaded
document.addEventListener('DOMContentLoaded', function() {
    // Select all elements with the 'scroll-reveal' class
    const scenes = document.querySelectorAll('.scroll-reveal');

    // Create an Intersection Observer to detect when elements come into view
    const observer = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
            // When an element becomes visible in the viewport
            if (entry.isIntersecting) {
                const element = entry.target;
                // Make the element fully visible (fade in)
                element.style.opacity = "1";
                // Reset the element's position to its original spot (slide animation)
                element.style.transform = "translateX(0)";
            }
        });
    }, {
        threshold: 0.2,  // Trigger when 20% of the element is visible
        rootMargin: '0px'  // No margin around the viewport
    });

    // Start observing all scene elements
    scenes.forEach(scene => observer.observe(scene));
});
</script>
